mapper.delete:

	delete able
	where abledate = #{ableDate}
	and hostno = #{hostNo}

mapper.update:

	update host
	set hostview = #{hostview}
	where hostno = #{hostNo}

mapper.select:

	select hostview
	from host
	where hostNo = #{hostNo}

mapper.update:

